import React, { useContext } from 'react';
import Link from 'next/link';
import {useQuery} from '@apollo/react-hooks';
import {AuthContext} from '../contexts/AuthProvider';
import {QUERY_PRODUCTS} from '../graphql/Products';
import Router from 'next/router';
import '../styles/globals.scss';

function Products() {
  const {data, loading, error} = useQuery(QUERY_PRODUCTS) //, {pollInterval:3000} Add pollInterval to refresh page per second
  const { user } = useContext(AuthContext)

  if(error) return <p>Ooops....! Something went wrong, Plz try again later</p>
  if(loading) return <p>loading...</p>
  return (
    <div className="container">
      {data.products.map(prod => 
          <div className="product-container" key={prod.id}>
            <Link href='/products/product_detail' as={`/products/${prod.id}`}>
              <a>
              <img src={prod.imageUrl} alt={prod.description} width="250px"/>
              </a>
            </Link>   
            <h3>{prod.name}</h3>
            <h4>{prod.price} THB</h4>
            {
              user && user.id === prod.user.id ? 
              <button className="btn-manage" onClick={() => Router.push('/manageProducts')}>Manage Products</button> :
              <button 
            >Add To Cart</button>
            }


          </div> 
      )}
    </div>
  )
}

export default Products;
