import React, { useContext } from 'react';
import Link from 'next/link';
import {AuthContext} from '../contexts/AuthProvider';
// const liStyle = {listStyle: "none"}
// const aStyle = {
//   color: 'white',
//   fontSize: '18px',
//   fontWeight: '4000',
//   textDecoration: 'none'
// }
function nav() {
  const {user, signout } = useContext(AuthContext)
  return (
    <nav className="site-navigation"
      style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '75px', 
        background: 'teal'
      }}
      >
      <span className="menu-title">EZ-Shoppin</span>
      <div className="menu-content-container">
      <span className="menu-title">
        {user && <>
          <li>
            <Link href="/">
              <a>@{user.username}</a>
            </Link>
          </li>
          </>
        }
      </span>
      <ul 
      // style={{
      //   display:'flex',
      //   justifyContent:'space-around',
      //   alignItems: 'center',
      //   width: '20%',

      // }}
      >
        <li>
          <Link href="/">
            <a>ຫນ້າຫລັກ</a>
          </Link>
        </li>

        <li>
          <Link href="/products">
            <a>ສີນຄ້າ</a>
          </Link>
        </li>

        {user && <>
          <li>
            <Link href="/carts">
              <a>Carts</a>
            </Link>
          </li>

          <button 
          // style={{
          //   background: 'teal',
          //   color:'white',
          //   fontSize: '18px',
          //   padding: '10px',
          //   border:'none',
          //   cursor:'pointer'
          // }}
          onClick={signout}
          >Logout</button>

           <li>
            <Link href="/manageProducts">
              <a>ສ້າງສີນຄ້າ</a>
            </Link>
          </li>
        </>}

        {!user && <>
            <li>
            <Link href="/signin">
              <a>Signin</a>
            </Link>
          </li>

          <li>
            <Link href="/signup">
              <a>Signup</a>
            </Link>
          </li>
        </>}
      </ul>
      </div>
    </nav>
  )
}

export default nav
