import React, {useState} from 'react';

const Signup = () => {
  const [userInfo, setUserInfo] = useState({
    username: '',
    email: '',
    password: ''
  })
  const handleChange = e => {
    setUserInfo({
      ...userInfo,
      [e.target.username]: e.target.value
    })
  }

  console.log(userInfo)
  return (
    <div style={{ margin: '100px'}}>
      <form action="" 
        style={{ 
          display:'flex', 
          flexDirection: 'column', 
          margin: 'auto', 
          width: '30%' 
        }}>
        <input 
          style={{ 
            margin: '5px', 
            height: '30px'
          }} 
          type="text" 
          name="username" 
          placeholder="Enter Username"
          onChange={handleChange}
          value={userInfo.username}
        />
        <input 
          style={{ 
            margin: '5px', 
            height: '30px'
          }} 
          type="email" 
          name="email" 
          placeholder="Enter Email"
          onChange={handleChange}
        />
        <input 
          style={{ 
            margin: '5px', 
            height: '30px'
          }} 
          type="password" 
          name="password" 
          placeholder="Enter Password"
          onChange={handleChange}
        />
        <button 
          style={{ 
            margin: '5px', 
            padding: '10px', 
            background: 'teal', 
            color:'white', 
            border:'none', 
            cursor: 'pointer',
            fontSize:'18px'
            }}
          >Submit</button>
      </form>
    </div>
  )
}

export default Signup
