import React, {useState} from 'react';
import {useMutation} from '@apollo/react-hooks';
import gql from 'graphql-tag';
import Link from 'next/link';


const SIGN_UP = gql`
 mutation SIGN_UP($username: String!, $email: String!, $password: String!) {
  signup(username: $username, email: $email, password: $password
  ){
    id
    username
    email
  }
}
`


const Signup = () => {
  const [userInfo, setUserInfo] = useState({
    username: '',
    email: '',
    password: ''
  })
  const handleChange = e => {
    setUserInfo({
      ...userInfo,
      [e.target.username]: e.target.value,
      [e.target.email]: e.target.value,
      [e.target.password]: e.target.value
    })
    console.log('Value', e.target.value)
  }


  const [success, setSuccess] = useState(false)
  const [signup, {loading, error}] = useMutation(SIGN_UP, {
    variables: {...userInfo}, 
    onCompleted: data => {
      if(data) {
        setSuccess(true)
        setUserInfo(
          {
            username: '',
            email: '',
            password: ''
          }
        )
      }
  }})

  const handleSubmit = async e => {
    try {
      e.preventDefault()
      await signup()
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <div style={{ margin: '100px'}}>
      <form action="" 
        style={{ 
          display:'flex', 
          flexDirection: 'column', 
          margin: 'auto', 
          width: '30%' 
        }} onSubmit={handleSubmit}>
        <input 
          style={{ 
            margin: '5px', 
            height: '30px'
          }} 
          type="text" 
          className="username"
          id="username"
          placeholder="Enter Username"
          value={userInfo.username.value}
          onChange={handleChange}
        />
        <input 
          style={{ 
            margin: '5px', 
            height: '30px'
          }} 
          type="email" 
          className="email" 
          placeholder="Enter Email"
          value={userInfo.email.value}
          onChange={handleChange}
        />
        <input 
          style={{ 
            margin: '5px', 
            height: '30px'
          }} 
          type="password" 
          id="password"
          className="password" 
          placeholder="Enter Password"
          value={userInfo.password.value}
          onChange={handleChange}
        />
        <button 
          style={{ 
            margin: '5px', 
            padding: '10px', 
            background: 'teal', 
            color:'white', 
            border:'none', 
            cursor: 'pointer',
            fontSize:'18px'
            }}
          type='submit'
          disabled={loading}>Submit</button>
      </form>
      <div>
        {success && <p>You Successfully Signed Up, Please 
          <Link href="/signin">
            <a>Signin</a>
          </Link>
        </p>}

        {error && <p style={{ color: 'red'}}>{error.graphQLErrors[0].message}</p>}
      </div>
    </div>
  )
}

export default Signup
