import React, {useState} from 'react';
import fetch from 'isomorphic-unfetch';
import {MY_PRODUCTS, CREATE_PRODUCT} from '../graphql/Products';

const UserProductItem = ({product}) => {
  const [edit, setEdit] = useState(false)
  return (
    <div style={{
        display: 'grid', 
        gridTemplateColumns: '1fr 1fr 1fr 1fr 2fr', 
        width:'100%',
        borderTop: '1px solid grey',
        borderBottom:'1px solid grey'
      }}
    >
      <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
        <img src={product.imageUrl} alt={product.description} width='80px'/>
      </div>
      <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
        <p>{product.name}</p>
      </div>
      <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
        <p>{product.description}</p>
      </div>
      <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
        <p>{product.price}THB</p>
      </div>
      <div style={{margin: 'auto', alignItems: 'center', display: 'flex', alignItems: 'center'}}>
        <button style={{ margin: '10px', background: 'orange', color: 'white', padding: '10px',  border: 'none'}}>Update</button>
        <button style={{ margin: '10px', background: 'red', color: 'white', padding: '10px',  border: 'none'}}>Delete</button>
      </div>
    </div>
  )
}

export default UserProductItem;
