import React, { useContext } from 'react';
import {useRouter} from 'next/router';
import {useQuery} from '@apollo/react-hooks';
import {QUERY_PRODUCT} from '../graphql/Products';
import {AuthContext} from '../contexts/AuthProvider';



function Product() {
  const route = useRouter()
  const {data, loading, error} = useQuery(QUERY_PRODUCT, {variables: {id: route.query.productId}})
  const { user } = useContext(AuthContext)
   console.log('result', data)
  if(error) return <p>Ooops....! Something went wrong, Plz try again later</p>
  if(loading) return <p>loading...</p>
  return (
    <div
      style={{
        display:'flex',
        flexDirection:'column',
        alignItems: 'center'
      }}
    >
      <img src={data.product.imageUrl} alt={data.product.description} width='375px' />
      <h3>{data.product.name}</h3>
      <h5>{data.product.price}</h5>
      <h5>{data.product.description}</h5>
      <button style={{ 
            background: 'green', 
            color: 'white', 
            padding: '10px', 
            cursor:'pointer', 
            border: 'none', 
            borderRadius:'10px'
      }}
      >Add To Cart</button>
    </div>
  )
}

export default Product;
