import React, { useContext, useEffect } from 'react';
import Link from 'next/link';
import { AuthContext } from '../contexts/AuthProvider';
import { useQuery } from '@apollo/react-hooks';
import { MY_PRODUCTS } from '../graphql/Products';
import { QUERY_USER } from '../graphql/User';


function nav() {
    const { user, signout, setAuthUser } = useContext(AuthContext)
    const { data } = useQuery(QUERY_USER)
    console.log(user)
    console.log(data)


    useEffect(() => {
        if (data) {
            setAuthUser(data.user)
        }
    }, [data])
    return (
        <nav className="site-navigation">
            <span className="menu-title">EZ-Shoppin</span>
            <div className="menu-content-container">
                <span className="menu-title">
                    {user && <>
                        <li className="username">
                            <Link href="/">
                                <a>@{user.username}</a>
                            </Link>
                        </li>
                    </>
                    }
                </span>
                <ul>
                    <li>
                        <Link href="/">
                            <a>Home</a>
                        </Link>
                    </li>

                        <li>
                            <Link href="/products">
                                <a>Products</a>
                            </Link>
                        </li>

                    {user && <>
                        <li>
                            <Link href="/carts">
                                <a>Carts <span
                                    style={{
                                        background: 'red',
                                        color: 'white',
                                        borderRadius: '50%',
                                        padding: '3px'
                                    }}
                                >
                                    {user && user.cart && user.cart.length === 0 && 0}
                                    {user && user.cart && user.cart.length > 0 && user.cart.reducer((sum, item) => sum + item.qualtity, 0)}
                                </span></a>
                            </Link>
                        </li>

                        <button className="btn-signout" onClick={signout}
                        >Logout</button>

                        <li>
                            <Link href="/manage">
                                <a>My Store</a>
                            </Link>
                        </li>
                    </>}

                    {!user && <>
                        <li>
                            <Link href="/signin">
                                <a>Signin</a>
                            </Link>
                        </li>

                        <li>
                            <Link href="/signup">
                                <a>Signup</a>
                            </Link>
                        </li>
                    </>}
                </ul>
            </div>
        </nav>
    )
}

export default nav;
