import React from "react";
import Modal from "../components/Modal";

class ModalPage extends React.Component {
    state = {
        show: false
    };
    showModal = e => {
        this.setState({
            show: !this.state.show
        });
    };
    render() {
        return (
            <>
                <button
                    class="toggle-button"
                    id="centered-toggle-button"
                    onClick={e => {
                        this.showModal(e);
                    }}
                >
                    {" "}
          show Modal{" "}
                </button>

                <Modal onClose={this.showModal} show={this.state.show}>
                    <label htmlFor="productname">Product Name:</label>
                    <input
                        type="text"
                        name="name"
                        placeholder="Enter Product Name"
                        value={productData.name}
                        onChange={handleChange}
                    />
                    <label htmlFor="address">Product Desc:</label>
                    <textarea
                        type="text"
                        name="description"
                        placeholder="Product Description"
                        value={productData.description}
                        onChange={handleChange}
                    />
                    <label htmlFor="city">Price:</label>
                    <input
                        type="number"
                        name="price"
                        placeholder="Price"
                        value={productData.price}
                        onChange={handleChange}
                    />
                    <label htmlFor="contact">Stock Qty:</label>
                    <input
                        type="text"
                        name="contact"
                        placeholder="Contact No."
                    />
                    <label htmlFor="contact">--Select Categories--</label>
                    <input
                        type="text"
                        name="contact"
                        placeholder="Contact No."
                    />
                    <label htmlFor="contact">--Select Brands--</label>
                    <input
                        type="text"
                        name="contact"
                        placeholder="Contact No."
                    />
                    <label htmlFor="contact">Product Image</label>
                    <input
                        type="file"
                        name="file"
                        placeholder="file *.png, *.jpg..."
                        onChange={selectFile}
                    />
                </Modal>
            </>
        );
    }
}

export default ModalPage;
