import React, { Component } from 'react'
import BankAcList from './BankAcList';
import Backdrop from '../Backdrop';
import Modal from '../Modal';


class UserBankAc extends Component {
    state = {
        creating: false
    };

    CreateEventHandler = () => {
        this.setState({ creating: true });
    };

    modalConfirmHandler = () => {
        this.setState({ creating: false });
    };

    modalCancelHandler = () => {
        this.setState({ creating: false });
    };

    _renderCreateBankAccount = () => () => {
        return(
            <div className="creat-bank__account__card">
                <form action="">
                    <div className="forn-control">

                    </div>
                </form>
            </div>
        )
    }

    render() {
        return (
            <>
                <div className="bank_card__head">
                    <h2 className="dash-title">My Bank Account</h2>
                    <a onClick={this.CreateEventHandler}>
                        <span className="ti-plus"></span>
                        <small>Add Account</small>
                    </a>
                </div>
                { this.state.creating && <Backdrop />}
                { this.state.creating && (
                    <Modal
                        title="Create Bank Account No"
                        canCancel
                        canConfirm
                        onCancel={this.modalCancelHandler}
                        onConfirm={this.modalConfirmHandler}
                    >
                        <p>I am from Modal</p>
                    </Modal>)}
                <BankAcList />
            </>
        )
    }
}

export default UserBankAc;
