        //     user && user.id === prod.user.id ? 
        //         <button className="btn-manage"
        //           onClick={() => Router.push('/manageProducts')}
        //         >Manage Products</button> :
        //         <button className="btn-add-carts"
        //         onClick={() => Router.push('/carts')}
        //         >Add To Cart</button>
        //       } 
