  const handleSubmit = async e => {
    try {
      e.preventDefault()
      // await login()
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <div style={{ margin: '100px'}}>
    <form
      style={{ 
        display:'flex', 
        flexDirection: 'column', 
        margin: 'auto', 
        width: '45%' 
      }} 
      onSubmit={handleSubmit} >
      <input 
        style={{ 
          margin: '5px', 
          height: '30px'
        }} 
        type="email" 
        name="email" 
        placeholder="Enter Email"
        value={userInfo.email}
        onChange={handleChange}
      />
      <input 
        style={{ 
          margin: '5px', 
          height: '30px'
        }} 
        type="password"
        name="password" 
        placeholder="Enter Password"
        value={userInfo.password}
        onChange={handleChange}
      />
       <div style={{width: '30%', margin: 'auto'}}>
       <p>Forgot password?{" "} <span style={{color: 'orange', cursor:'pointer'}} onClick={() => Router.push('/signin/resetpassword')}>Click here</span></p>
      </div>
      <button 
        style={{ 
          margin: '5px', 
          padding: '10px', 
          background: 'teal', 
          color:'white', 
          border:'none', 
          cursor: 'pointer',
          fontSize:'18px'
          }}
        type='submit'
        disabled={loading}>Login</button>
      <div style={{width: '30%', margin: 'auto'}}>
        {error && <p style={{ color: 'red'}}>{error.graphQLErrors[0].message}</p>}
      </div>
      <Link href="/signup">
        <a>Don't have an account? Signup here</a>
