import React from 'react';
import {useQuery} from '@apollo/react-hooks';
import {MY_PRODUCTS} from '../graphql/Products';

const UserProducts = () => {
  const { data, loading, error} = useQuery(MY_PRODUCTS)

  //console.log(data)
  return (
    <div style={{width: '50%', margin: 'auto'}}>
      {/* Header */}
      <div style={{display: 'grid', gridTemplateColumns: '1fr 1fr 1fr 1fr 2fr', width:'100%'}}>
        <h3 style={{margin: 'auto'}}>Image</h3>
        <h3 style={{margin: 'auto'}}>Name</h3>
        <h3 style={{margin: 'auto'}}>Description</h3>
        <h3 style={{margin: 'auto'}}>Price</h3>
        <h3 style={{margin: 'auto'}}>Actions</h3>
      </div>


      {/* Body */}
      { 
        data && 
        data.user && 
        data.user.products.length > 0 && 
        data.user.products.map(product => 
          <div key={product.id} style={{
              display: 'grid', 
              gridTemplateColumns: '1fr 1fr 1fr 1fr 2fr', 
              width:'100%',
              borderTop: '1px solid grey',
              borderBottom:'1px solid grey'
            }}
          >
          <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
            <img src={product.imageUrl} alt={product.description} width='80px'/>
          </div>
          <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
            <p>{product.name}</p>
          </div>
          <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
            <p>{product.description}</p>
          </div>
          <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
            <p>{product.price}THB</p>
          </div>
          <div style={{margin: 'auto', alignItems: 'center', display: 'flex', alignItems: 'center'}}>
            <button style={{ margin: '10px', background: 'orange', color: 'white', padding: '10px',  border: 'none'}}>Update</button>
            <button style={{ margin: '10px', background: 'red', color: 'white', padding: '10px',  border: 'none'}}>Delete</button>
          </div>
        </div>

      )}
      </div>
  )
}

export default UserProducts
