import React, { useState, useContext, useEffect } from 'react';
import Link from 'next/link';
import { AuthContext } from '../../contexts/AuthProvider';
import { useQuery } from '@apollo/react-hooks';
import { MY_PRODUCTS } from '../../graphql/Products';

import { Avatar } from 'antd';


const navLink = [
    {
        title: 'HOME',
        path:'/'
    },
    {
        title: 'PRODUCTS',
        path:'/products'
    },
    {
        title: 'CARTS',
        path:'/carts'
    },
    {
        title: 'SIGNIN',
        path:'/signin'
    },
    {
        title: 'SIGNUP',
        path:'/signup'
    }
]

function Navigation() {
    const [menuActive, setMenuActive] = useState(false);
    const { user, signout, setAuthUser } = useContext(AuthContext);
    const { data } = useQuery(MY_PRODUCTS);


    useEffect(() => {
        if(data){
            setAuthUser(data.user)
        }
    }, [data])
    return(
        <nav className="site-navigation">
            <span className="menu-title">Cubiz Store</span>
           <div className={`menu-content-container ${menuActive && 'active'}`}>
                <ul>
                        {!user && 
                            navLink.map((link, index) => (
                                <li key={index}>
                                    <Link href={link.path}>
                                        <a>{link.title}</a>
                                    </Link>
                                </li>
                            ))}
                        {
                            user && 
                                <>
                                    <li>
                                        <Link href="/">
                                            <a>HOME</a>
                                        </Link>
                                    </li>
                                    <li>
                                        <Link href="/products">
                                            <a>PRODUCTS</a>
                                        </Link>
                                    </li>
                                    <li>
                                        <Link href="/carts">
                                            <a>CARTS 

                                                {/* <span
                                                style={{
                                                    background: 'red',
                                                    color: 'white',
                                                    borderRadius: '50%',
                                                    padding: '3px'
                                                }}
                                            >
                                                {user && user.cart && user.cart.length === 0 && 0}
                                                {user && user.cart && user.cart.length > 0 && user.cart.reducer((sum, item) => sum + item.qualtity, 0)}
                                            </span> */}
                                            </a>
                                        </Link>
                                    </li>
                                    <li>
                                        <Link href="/dashboard">
                                            <a>MY STORE</a>
                                        </Link>
                                    </li>
                                    <button className="btn-signout" onClick={signout}
                                    >Logout</button>

                                </>
                        }
                    </ul>
                    <span className="avatar-container">
                       {
                           user && 
                           <>
                            <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" size={40} />
                            <span className="menu-avatar-name">@{user.username}</span>
                           </>
                       }
                    </span>
           </div>
           <i className="ionicons icon ion-ios-menu" onClick={() => setMenuActive(!menuActive)} />
        </nav>
    )
}

export default Navigation;
