import React, { useContext, useState } from 'react';
import { AuthContext } from '../../contexts/AuthProvider';
import Router from 'next/router';
import UserProductItems from './UserProductItems';
import Backdrop from '../Backdrop';
import Modal from '../Modal';


const UserProducts = () => {

    const [state ] = {
        creating: false
    }



    const { user } = useContext(AuthContext)

    const CreateEventHandler = () => {
        setState(true)
    }

    const modalConfirmHandler = () => {
        setState(false)
    }

    const modalCancelHandler = () => {
        setState(false)
    }
    //console.log("User Product:", user)
    return (
        <>
            { state.creating && <Backdrop />}
            { state.creating &&
                <Modal title="Create Product" canCancel canConfirm onCancel={modalCancelHandler} onConfirm={modalConfirmHandler}>
                    <p>Modal content</p>
                </Modal>}
            <section className="recent-products">
                <div className="products-grid">
                    <div className="products-card">
                        <div className="product-card-header">
                            <h3>Recently products List</h3>
                            {/* <button onClick={() => Router.push('/userProducts/createProduct')}>
                                <span className="ti-plus"></span>
                                <small>Add Product</small>
                            </button> */}
                            <button onClick={CreateEventHandler}>
                                <span className="ti-plus"></span>
                                <small>Add Product</small>
                            </button>
                        </div>
                        <div className="table-responsive">
                            <table>
                                <thead>
                                    <tr>
                                        <th>Image</th>
                                        <th>Products Name</th>
                                        <th>Description</th>
                                        <th>Price</th>
                                        <th>Stock Qty</th>
                                        <th>Unit</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        user &&
                                        user.products.length > 0 &&
                                        user.products.map(product => (
                                            <UserProductItems key={product.id} product={product} />
                                        ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </section>
        </>
    )
}

export default UserProducts;
