import React, { useContext } from 'react';
import Link from 'next/link';
import {AuthContext} from '../contexts/AuthProvider'
import '../styles/_nav.scss';
// const liStyle = {listStyle: "none"}
// const aStyle = {
//   color: 'white',
//   fontSize: '18px',
//   fontWeight: '4000',
//   textDecoration: 'none'
// }
function nav() {
  const {user, signout } = useContext(AuthContext)
  console.log(user)
  return (
    <nav className="site-navigation">
      <span className="menu-title">EZ-Shoppin</span>
      <div className="menu-content-container">
      <span className="menu-title">
        {user && <>
          <li className="username">
            <Link href="/">
              <a>@{user.username}</a>
            </Link>
          </li>
          </>
        }
      </span>
      <ul>
        <li>
          <Link href="/">
            <a>ຫນ້າຫລັກ</a>
          </Link>
        </li>

        <li>
          <Link href="/products">
            <a>ສີນຄ້າ</a>
          </Link>
        </li>

        {user && <>
          <li>
            <Link href="/carts">
              <a>Carts <span  
                style={{background: 'red', 
                                color: 'white', 
                                borderRadius: '50%', 
                                padding: '3px'}}
                                >
                               {user && user.carts && user.carts.lenght}     
                            </span></a>
            </Link>
          </li>

          <button className="btn-signout"  onClick={signout}
          >Logout</button>

           <li>
            <Link href="/manageProducts">
              <a>My Store</a>
            </Link>
          </li>
        </>}

        {!user && <>
            <li>
            <Link href="/signin">
              <a>Signin</a>
            </Link>
          </li>

          <li>
            <Link href="/signup">
              <a>Signup</a>
            </Link>
          </li>
        </>}
      </ul>
      </div>
    </nav>
  )
}

export default nav;
