import React, { useState } from 'react';
import {useMutation} from '@apollo/react-hooks';
import gql from 'graphql-tools'


const CREATE_PRODUCT = gql`
  mutation CREATE_PRODUCT($name: String!, $description: String!, $price: Float!, $imageUrl: String!){
    createProduct(
        name: $name
        description: $description
        price: $price
        imageUrl: $imageUrl
      ){
        id
        name
        description
        price
        imageUrl
      }
  }
`

const ManageProducts = () => {
  const [productData, setProductData] = useState({
    name: '',
    description: '',
    price: '',
    imageUrl: ''
  })

  const handleChange = e => setProductData({
    ...productData, 
    [e.target.name]: e.target.value
  })




  const handleSubmit = async e => {
    try {
      e.preventDefault()
      // await signup()
      console.log(productData)
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <div style={{ margin: '100px'}}>
    <form style={{ display:'flex', flexDirection: 'column', margin: 'auto', width: '45%' }} 
      onSubmit={handleSubmit} >
       <h3>Create My Product</h3>
      <input style={{ margin: '5px', height: '30px' }} 
        type="text" 
        name="name"
        placeholder="Enter product name"
        value={productData.name}
        onChange={handleChange}
      />
      <input style={{ margin: '5px', height: '30px' }} 
        type="text" 
        name="description" 
        placeholder="Enter product description"
        value={productData.description}
        onChange={handleChange}
      />
      <input style={{ margin: '5px', height: '30px' }} 
        type="number"
        name="price" 
        placeholder="Enter Product Price"
        value={productData.price}
        onChange={handleChange}
      />
      <input style={{ margin: '5px', height: '30px' }} 
        type="text"
        name="imageUrl" 
        placeholder="Enter Product Image"
        value={productData.imageUrl}
        onChange={handleChange}
      />
      <button style={{ margin: '5px', padding: '10px', background: 'teal', color:'white', border:'none', cursor: 'pointer', fontSize:'18px' }}
              type='submit'
       // disabled={loading}
        >Submit</button>

      <div style={{width: '100%', margin: 'auto'}}>
        {/* {error && <p style={{ color: 'red'}}>{error.graphQLErrors[0].message}</p>} */}
        {(!productData.name || !productData.description || !productData.price || !productData.imageUrl) && <p style={{ color: 'red'}}>Please Fill all product required...!</p> }
      </div>
    </form>
  </div>
  )
}

export default ManageProducts
