import React, { useContext } from 'react';
import Link from 'next/link';
import {AuthContext} from '../contexts/AuthProvider'

const liStyle = {listStyle: "none"}
const aStyle = {
  color: 'white',
  fontSize: '18px',
  fontWeight: '4000',
  textDecoration: 'none'
}
function nav() {
  const {user, signout } = useContext(AuthContext)
  return (
    <nav className="site-navigation"
      // style={{ 
      //   display: 'flex', 
      //   justifyContent: 'center', 
      //   alignItems: 'center', 
      //   height: '75px', 
      //   background: 'teal'
      // }}
      >
      <span className="menu-title">EZ-Shoppin</span>
      <div className="menu-content-container">
      <ul style={{
        display:'flex',
        justifyContent:'space-around',
        alignItems: 'center',
        width: '20%',

        }}>
        {user && <>
          <li style={liStyle}>
            <Link href="/">
              <a style={aStyle}>@{user.username}</a>
            </Link>
          </li>
          </>
        }
      </ul>
      <ul style={{
        display:'flex',
        justifyContent:'space-around',
        alignItems: 'center',
        width: '20%',

      }}>
        <li style={liStyle}>
          <Link href="/">
            <a style={aStyle}>ຫນ້າຫລັກ</a>
          </Link>
        </li>

        <li style={liStyle}>
          <Link href="/products">
            <a style={aStyle}>ສີນຄ້າ</a>
          </Link>
        </li>

        {user && <>
          <li style={liStyle}>
            <Link href="/carts">
              <a style={aStyle}>Carts</a>
            </Link>
          </li>

          <button style={{
            background: 'teal',
            color:'white',
            fontSize: '18px',
            padding: '10px',
            border:'none',
            cursor:'pointer'
          }}
          onClick={signout}
          >Logout</button>

           <li style={liStyle}>
            <Link href="/manageProducts">
              <a style={aStyle}>ສ້າງສີນຄ້າ</a>
            </Link>
          </li>
        </>}

        {!user && <>
            <li style={liStyle}>
            <Link href="/signin">
              <a style={aStyle}>Signin</a>
            </Link>
          </li>

          <li style={liStyle}>
            <Link href="/signup">
              <a style={aStyle}>Signup</a>
            </Link>
          </li>
        </>}
      </ul>
      </div>
    </nav>
  )
}

export default nav
