import React, { useContext } from 'react';
import { useQuery, useMutation } from '@apollo/react-hooks';
import { MY_PROFILE } from '../../graphql/Profile';
import { AuthContext } from '../../contexts/AuthProvider';

const Profile = () => {
    //onst { user } = useContext(AuthContext);
    const { data, loading, error } = useQuery(MY_PROFILE)

    if(error) return <p>Oooops...Something went wrong!</p>
    if(loading) return <p>Loading...!</p>
    // console.log('User Profile id:', data.user.profile.id);
    // console.log('User Profile firstname:', data.user.profile.firstname);
    // console.log('User Profile lastname:', data.user.profile.lastname);
    // console.log('User Profile birthdate:', data.user.profile.birthdate);
    // console.log('User Profile age:', data.user.profile.age);
    // console.log('User Profile mental Status:', data.user.profile.mentalStatus);
    // console.log('User Profile Image:', data.user.profile.profileImage);

    // console.log('data profile:', data)
    // console.log('user data:', user)
    return (
        <>
        {data.user.profile === null ? <button>Create Profile</button> :
       <div>
           <h1>Profile Detail</h1>
          <span>{ data.user.profile.id}</span>
<span>{data.user.profile.firstname}</span>
<span>{data.user.profile.lastname}</span>
<span>data.user.profile.birthdate}</span>
 <span>{data.user.profile.age}</span>
<span>{data.user.profile.mentalStatus}</span>
<span>{data.user.profile.profileImage}</span>
       </div>}
       </>
    )
}

export default Profile;
