function MyApp({ Component, pageProps, apollo, user }) {

  return (
    <ApolloProvider client={apollo}>
      <AuthProvider userData={user}>
        <Layout>
          <Component {...pageProps} />
        </Layout>
      </AuthProvider>
    </ApolloProvider>
  )
}

MyApp.getInitialProps = async ({ctx, router}) => {
  // calls page's `getInitialProps` and fills `appProps.pageProps`
  if(process.browser) {
    return __NEXT_DATA__.props.pageProps
  }

  //console.log('Router -->', router)

  const {headers} = ctx.req;
  const cookies = headers && cookie.parse(headers.cookie || '')
  const token = cookies && cookies.jwtToken;

  if(!token) {
    if(
        router.pathname === '/carts' || 
        router.pathname === '/manage' ||  
        router.pathname === '/manage/createProduct'
    ) { //Use OR To Protected more Routers
      ctx.res.writeHead(302, {Location: '/signin'}) //302 Redirect Route code
      ctx.res.end()
    }
    //Add More Protected route here
    return null
  }
  const response = await fetch("http://localhost:5000/graphql", {
    method: 'post',
    headers: {
      'Content-type': 'application/json',
      authorization: `Bearer ${token}` || ""
    },
    body: JSON.stringify(QUERY_USER)
  })
//   console.log("response data:", response)
  if(response.ok){
        const result = await response.json()
        return {user: result.data.user.carts}
        //console.log('User Info -->',  result.data.user)
        // if(result.data.user.carts === null || result.data.user.carts === undefined){
        //     ctx.res.writeHead(302, {Location: '/signin'})
        // }
  } else {
    if(router.pathname === "/carts") {
      ctx.res.writeHead(302, {Location: '/signin'}) //302 Redirect Route code
      ctx.res.end()
    }
     //Add More Protected route here
    return null
  }
  // console.log(ctx.req.headers)
}

export default apolloClient(MyApp)
