import {useMutation} from '@apollo/react-hooks';
import gql from 'graphql-tag';
import Link from 'next/link';
import Router from 'next/router';
import Cookies from 'js-cookie';


const REQUEST_RESET_PASSWORD = gql`
 mutation REQUEST_RESET_PASSWORD($email: String!) {
    requestResetPassword(email: $email){
      message
    }
}
`
const ResetPasswordPage = () => {
  const [email, setEmail] = useState('')
  const handleChange = e => {
    // setUserInfo({
    //   ...userInfo,
    //   [e.target.name]: e.target.value, //Target Name and Value
    // })    
  }

  const [requestResetPassword, {loading, error}] = useMutation(REQUEST_RESET_PASSWORD, {
    variables: {...userInfo}, 
    onCompleted: data => {
      if(data) {
        setAuthUser(data.login.user)
        //Set cookie here
        Cookies.set('jwtToken', data.login.jwt)
        setUserInfo({
            email: "",
            password: ""
        })
        Router.push('/products')
      }
  }})


  const handleSubmit = async e => {
    try {
      e.preventDefault()
      // await login()
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <div style={{ margin: '100px'}}>
    <form
      style={{ 
        display:'flex', 
        flexDirection: 'column', 
        margin: 'auto', 
        width: '45%' 
      }} 
      onSubmit={handleSubmit} >
      <h3>Reset Password Form</h3>
      <input 
        style={{ 
          margin: '5px', 
          height: '30px'
        }} 
        type="email" 
        name="email" 
        placeholder="Enter Email"
        //value={userInfo.email}
        onChange={handleChange}
      />
      <div style={{width: '30%', margin: 'auto'}}>
        {/* {error && <p style={{ color: 'red'}}>{error.graphQLErrors[0].message}</p>} */}
      </div>
      <button 
        style={{ 
          margin: '5px', 
          padding: '10px', 
          background: 'teal', 
          color:'white', 
          border:'none', 
          cursor: 'pointer',
          fontSize:'18px'
          }}
        type='submit'
        //disabled={loading}
        >Submit</button>
    </form>

  </div>
  )
}

export default ResetPasswordPage
