import React, { useContext } from 'react';
import { useQuery, useMutation } from '@apollo/react-hooks';
import { MY_PROFILE } from '../../graphql/Profile';
import { AuthContext } from '../../contexts/AuthProvider';
import Router  from 'next/router';
import dayjs from 'dayjs';



const Profile = () => {
    //onst { user } = useContext(AuthContext);
    const { data, loading, error } = useQuery(MY_PROFILE, )
    console.log("User data:", data)
    if (error) return <p>Oooops...Something went wrong!</p>
    if (loading) return <p>Loading...!</p>

    //const birthdate = data.user.profile.birthdate;
    return (

        <div className="profile_page">
            {data.user.profile === null ? <button onClick={() => Router.push('/profile/create')}>Create Profile</button> :
                <div>
                      {/* Create Thumnail for no-cover-iamge */}
                      <div>
                        <img src={data.user.profile.coverImage} alt="cover_image"/>
                    </div>
                     {/* Create Thumnail for no-profile-iamge */}
                    <div>
                        <img src={data.user.profile.profileImage} alt="profile_image"/>
                    </div>
                    <h3>Personal Info:</h3>
                    <span>Firstname: {data.user.profile.firstname}</span><br/>
                    <span>Lastname: {data.user.profile.lastname}</span><br/>
                    <span>Birthdate: {dayjs(data.user.profile.birthdate).format('MMM-DD-YYYY')}</span><br/>
                    <span>Age: {data.user.profile.age}</span><br/>
                    <span>Status: {data.user.profile.mentalStatus}</span><br/>
                </div>}
                <br/>
                <div>
                    <h3>Contact Info:</h3>
                    <span>Email: {data.user.email}</span>
                </div>
                <div>
                    <h3>Current Address:</h3>
                </div>
                <div>
                    <h3>Hometown Address:</h3>
                </div>
                <div>
                    <button>Cancel</button>
                    <button>Update</button>
                </div>
        </div>
    )
}

export default Profile;
