import React, {useState} from 'react';
import fetch from 'isomorphic-unfetch';
import {MY_PRODUCTS, CREATE_PRODUCT} from '../graphql/Products';

const UserProductItem = ({product}) => {
  const [edit, setEdit] = useState(false)

    //Create State
    const [file, setFile] = useState(null)
    const [productData, setProductData] = useState({
      name: '',
      description: '',
      price: '',
      imageUrl: ''
    })

    // const [createProduct, {loading, error}] = useMutation(CREATE_PRODUCT, {
    //   variables: {...productData, price: +productData.price},
    //   refetchQueries:[{query: QUERY_PRODUCTS}, {query: MY_PRODUCTS}]
    // })

    const handleChange = e => setProductData({
      ...productData, 
      [e.target.name]: e.target.value
    })

    const selectFile = e => {
      const files = e.target.files
      setFile(files[0])
      console.log(files)
    } 

    //Upload File to Cloudinary
    const uploadFile = async () => {
      const data = new FormData()
      data.append('file', file)
      data.append('upload_preset', 'shoppin')
      const res = await fetch('https://api.cloudinary.com/v1_1/swizce/image/upload', {
        method: 'post',
        body: data
      })

      const result = await res.json()

      console.log(result)
    }
    const handleSubmit = async e => {
      try {
        e.preventDefault()
        const result = await uploadFile()
        //const result = await createProduct()
        console.log(result)
      } catch (error) {
        console.log(error)
      }
    }
  return (
    <div style={{
        display: 'grid', 
        gridTemplateColumns: '1fr 1fr 1fr 1fr 2fr', 
        width:'100%',
        borderTop: '1px solid grey',
        borderBottom:'1px solid grey'
      }}
    >
      <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
        {!edit ? <img src={product.imageUrl} alt={product.description} width='80px'/> :
          <input type='file' name='file' onChange={selectFile} />
        }
      </div>
      <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
       {!edit ? (
          <p>{product.name}</p>
       ): (
         <input type="text" name="name" value={product.name}  onChange={handleChange}/>
       )
      }
      </div>
      <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
        {!edit ? (
          <p>{product.description}</p>
       ): (
         <input type="text" name="description" value={product.description}  onChange={handleChange}/>
       )
      }
      </div>
      <div style={{margin: 'auto', alignItems: 'center', marginBottom:'5px'}}>
        {!edit ? (
          <p>{product.price}THB</p>
       ): (
         <input type="text" name="price" value={product.price}  onChange={handleChange}/>
       )
      }
      </div>
      <div style={{margin: 'auto', alignItems: 'center', display: 'flex', alignItems: 'center'}}>
        <button style={{ margin: '10px', background: 'orange', color: 'white', padding: '10px',  border: 'none'}}>Update</button>
        <button style={{ margin: '10px', background: 'red', color: 'white', padding: '10px',  border: 'none'}}>Delete</button>
      </div>
    </div>
  )
}

export default UserProductItem;
