import React, { useContext, Component } from 'react';
import { AuthContext } from '../../contexts/AuthProvider';
import Router from 'next/router';
import UserProductItems from './UserProductItems';
import Backdrop from '../Backdrop';
import Modal from '../Modal';

const { user } = useContext(AuthContext)

class UserProduct extends Component {

    state = {
        creating: false
    }

    CreateEventHandler = () => {
        this.setState({ creating: true })
    }

    modalConfirmHandler = () => {
        this.setState({ creating: true })
    }

    modalCancelHandler = () => {
        this.setState({ creating: false })
    }
    //console.log("User Product:", user)

    render() {

        return (
            <>


                <section className="recent-products">
                    <div className="products-grid">
                        <div className="products-card">
                            <div className="product-card-header">
                                <h3>Recently products List</h3>
                                {/* <button onClick={() => Router.push('/userProducts/createProduct')}>
                                <span className="ti-plus"></span>
                                <small>Add Product</small>
                            </button> */}
                                <button onClick={this.CreateEventHandler}>
                                    <span className="ti-plus"></span>
                                    <small>Add Product</small>
                                </button>
                            </div>
                            <div className="table-responsive">
                                <table>
                                    <thead>
                                        <tr>
                                            <th>Image</th>
                                            <th>Products Name</th>
                                            <th>Description</th>
                                            <th>Price</th>
                                            <th>Stock Qty</th>
                                            <th>Unit</th>
                                            <th>Actions</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            user &&
                                            user.products.length > 0 &&
                                            user.products.map(product => (
                                                <UserProductItems key={product.id} product={product} />
                                            ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </section>
            </>
        )
    }
}

export default UserProduct;
