import React, { useContext } from 'react';
// import Carts from '../../components/Carts';
// import { AuthContext } from '../../contexts/AuthProvider';
import { useQuery } from '@apollo/react-hooks';
import { MY_CARTS } from '../../graphql/Carts';
import CartItem from './CartItem';

const CartPage = () => {
    const { data, loading, error } = useQuery(MY_CARTS) //
    if (error) return <p>Ooops....! Something went wrong, Plz try again later</p>
    if (loading) return <p>Empty Cart Loading...</p>


    return (
        <div className="cart-page">
            <div className="cart-content">
                {/* <Carts /> */}
                <div className="table-responsive">
                                    <table>
                                        <thead>
                                                <tr>
                                                    <th>Image</th>
                                                    <th>Products Name</th>
                                                    <th>Price</th>
                                                    <th>Qty</th>
                                                    <th>Actions</th>
                                                 </tr>
                                        </thead>
                                        <tbody>
                                                {
                                                    data.user.carts.map(cart => 
                                                        <CartItem key={cart.id} cart={cart} />
                                                    )
                                                }
                                        </tbody>
                                    </table>
                            </div>
            </div>
        </div>
    )
}

export default CartPage;
