    state = {
        creating: false
    };

    CreateEventHandler = () => {
        this.setState({ creating: true });
    };

    modalConfirmHandler = () => {
        this.setState({ creating: false });
    };

    modalCancelHandler = () => {
        this.setState({ creating: false });
    };

    render() {
        return (
            <>
                <div className="brands-card__head">
                    <h2 className="dash-title">All Product Brands List</h2>
