import React, { Component } from 'react';
import Backdrop from '../Backdrop';
import Modal from '../Modal';




class Brands extends Component {
    state = {
        creating: false
    };

    CreateEventHandler = () => {
        this.setState({ creating: true });
    };

    modalConfirmHandler = () => {
        this.setState({ creating: false });
    };

    modalCancelHandler = () => {
        this.setState({ creating: false });
    };

    render() {
        return (
            <>
                <div className="brands-card__head">
                    <h2 className="dash-title">All Product Brands List</h2>
                    <a onClick={this.CreateEventHandler}>
                        <span className="ti-plus"></span>
                        <small>Add Brand</small>
                    </a>
                </div>

                <section className="recent-brand">
                    <div className="brands-grid">
                        <div className="brands-card">
                            <h3>Recently Brands List</h3>

                            <div className="table-responsive">
                                <table>
                                    <thead>
                                        <tr>
                                            <th>Image</th>
                                            <th>Brand Name</th>
                                            <th>Description</th>
                                            <th>Actions</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr>
                                            <td className="td-team">
                                                <div className="img-1"></div>
                                            </td>
                                            <td>Apple</td>
                                            <td>23 Dec, 2020</td>
                                            <td>
                                                <span className="badge success">Success</span>
                                            </td>
                                        </tr>

                                        <tr>
                                            <td className="td-team">
                                                <div className="img-1"></div>
                                            </td>
                                            <td>Samsung</td>
                                            <td>23 Dec, 2020</td>
                                            <td>
                                                <span className="badge success">Success</span>
                                            </td>
                                        </tr>

                                        <tr>
                                            <td className="td-team">
                                                <div className="img-1"></div>
                                            </td>
                                            <td>Huawei</td>
                                            <td>23 Dec, 2020</td>
                                            <td>
                                                <span className="badge success">Success</span>
                                            </td>
                                        </tr>

                                        <tr>
                                            <td className="td-team">
                                                <div className="img-1"></div>
                                            </td>
                                            <td>Huawei</td>
                                            <td>23 Dec, 2020</td>
                                            <td>
                                                <span className="badge success">Success</span>
                                            </td>
                                        </tr>

                                        <tr>
                                            <td className="td-team">
                                                <div className="img-1"></div>
                                            </td>
                                            <td>Huawei</td>
                                            <td>23 Dec, 2020</td>
                                            <td>
                                                <span className="badge success">Success</span>
                                            </td>
                                        </tr>

                                        <tr>
                                            <td className="td-team">
                                                <div className="img-1"></div>
                                            </td>
                                            <td>Huawei</td>
                                            <td>23 Dec, 2020</td>
                                            <td>
                                                <span className="badge success">Success</span>
                                            </td>
                                        </tr>

                                        <tr>
                                            <td className="td-team">
                                                <div className="img-1"></div>
                                            </td>
                                            <td>Huawei</td>
                                            <td>23 Dec, 2020</td>
                                            <td>
                                                <span className="badge success">Success</span>
                                            </td>
                                        </tr>

                                        <tr>
                                            <td className="td-team">
                                                <div className="img-1"></div>
                                            </td>
                                            <td>One Plus</td>
                                            <td>23 Dec, 2020</td>
                                            <td>
                                                <span className="badge success">Success</span>
                                            </td>
                                        </tr>

                                        <tr>
                                            <td className="td-team">
                                                <div className="img-1"></div>
                                            </td>
                                            <td>Oppo</td>
                                            <td>23 Dec, 2020</td>
                                            <td>
                                                <span className="badge success">Success</span>
                                            </td>
                                        </tr>

                                    </tbody>
                                </table>
                            </div>
                        </div>

                        <div className="brand-detail">
                            <div className="detail-card">
                                {/* <div className="detail-single">
                                                        <span className="ti-id-badge"></span>
                                                        <h3>196</h3>
                                                        <small>Number Of Staff</small>
