import React from 'react';
import { useQuery } from '@apollo/react-hooks';
import { MY_CARTS } from '../../graphql/Carts';
import CheckOutItems from './CheckOutItems';



const CheckOutList = () => {
    const { data, loading, error } = useQuery(MY_CARTS) //
    // console.log("User Cart:", data)
    if (error) return <p>Ooops....! Something went wrong, Plz Login try again later</p>
    if (loading) return <p>Empty Cart Loading...</p>
    return (
        <>
            <div className="summary-checkout">
                <h3 className="bill-title">Summary Product:</h3>
                <div className="table-responsive">
                    <table>
                        <thead>
                            <tr>
                                <th>Product</th>
                                <th>Price</th>
                                <th>Qty</th>
                                <th>Total Price</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                data.user.carts.map(item =>
                                    <CheckOutItems key={item.id} item={item} />
                                )
                            }
                        </tbody>
                        <tfoot>
                            {
                                data.user.carts.map(item =>
                                    <CheckOutItems key={item.id} item={item} />
                                )
                            }

                            <tr>
                                <th></th>
                                <th></th>
                                <th>Total Anount:</th>
                                <th>$23,459.00</th>
                            </tr>
                        </tfoot>
                    </table>
                </div>
            </div>
        </>
    )
}

export default CheckOutList;
