import React, { Component, useState } from 'react'
import ProductList from './ProductList';
import Backdrop from '../Backdrop';
import Modal from '../Modal';
import CreateProduct from './CreateProduct';


import { useMutation } from '@apollo/react-hooks';
import fetch from 'isomorphic-unfetch';
import Router from 'next/router';
import { QUERY_PRODUCTS, CREATE_PRODUCT, UPDATE_PRODUCT, MY_PRODUCTS } from '../../graphql/Products';


class UserProduct extends Component {
    state = {
        creating: false
    };

    constructor(props) {
        super(props);
        this.nameElRef = React.createRef();
        this.descriptionElRef = React.createRef();
        this.priceElRef = React.createRef();
        this.imageUrlElRef = React.createRef();
    }
    CreateEventHandler = () => {
        this.setState({ creating: true });
    };

    modalConfirmHandler = () => {
        this.setState({ creating: false });
        this.name = this.nameElRef.current.value;
        this.description = this.descriptionElRef.current.value;
        this.price = this.priceElRef.current.value;
        this.imageUrl = this.imageUrlElRef.current.value;

        const event =  {name, description, price, imageUrl};
        console.log(event)
    };

    modalCancelHandler = () => {
        this.setState({ creating: false });
    };
    /**
     * Create Form Component with Function
     */
    _renderCreateProduct = () => () => {
        const [edit, setEdit] = useState(false)
        const [file, setFile] = useState(null)
        const [productData, setProductData] = useState({
            name: '',
            description: '',
            price: '',
            imageUrl: ''
        })

        const [createProduct, { loading, error }] = useMutation(CREATE_PRODUCT, {
            refetchQueries: [{ query: QUERY_PRODUCTS }]
        })

        const handleChange = e => setProductData({
            ...productData,
            [e.target.name]: e.target.value
        })

        const selectFile = e => {
            const files = e.target.files
            setFile(files[0])
            //console.log(files)
        }

        //Upload File to Cloudinary
        const uploadFile = async () => {
            const data = new FormData()
            data.append('file', file)
            data.append('upload_preset', 'shoppin')
            const res = await fetch('https://api.cloudinary.com/v1_1/swizce/image/upload', {
                method: 'post',
                body: data
            })
            const result = await res.json()
            //console.log('image result',  result)
            return result.secure_url
        }

        const handleSubmit = async e => {
            try {
                e.preventDefault()
                const url = await uploadFile()
                // console.log('image Url:', url)
                if (url) {
                    const result = await createProduct({
                        variables: {
                            ...productData,
                            price: +productData.price,
                            imageUrl: url
                        },
                    })
                    //console.log('Product Data:', result)
                    return result;
                }
                Router.push('/userProducts')
            } catch (error) {
                console.log(error)
            }
        }
        return (
            <div className="create-product-card">
                <div className="create-product_tabs">
                    <h5>create More tabs here</h5>
                    <ul>
                        <li><a href="">Product Basic Info</a></li>
                        <li><a href="">Product Image Gallary</a></li>
                        <li><a href="">Product Promotion</a></li>
                        <li><a href="">Product Slug detail</a></li>
                        <li><a href="">Product Release QR-Code</a></li>
                    </ul>
                </div>
                <div className="create-product_form">
