import React, { useState } from 'react';
import { useMutation } from '@apollo/react-hooks';
import { REQUEST_RESET_PASSWORD } from '../graphql/User';
import '../styles/_login.scss';
const RequestResetPassword = () => {
    const [email, setEmail] = useState('')
    const [message, setMessage] = useState('')


    const [requestResetPassword, { loading, error }] = useMutation(REQUEST_RESET_PASSWORD, {
        variables: { email },
        onCompleted: data => {
            if (data) {
                setMessage(data.requestResetPassword.message)
            }
        }
    })

    const handleChange = e => {
        setEmail(e.target.value)
    }
    const handleSubmit = async e => {
        try {
            e.preventDefault()
            await requestResetPassword()
        } catch (error) {
            console.log(error)
        }
    }

    return (
        <div className="login-page">
            <form  onSubmit={handleSubmit} >
                <h3>Request To Reset Password Form</h3>
                <hr></hr>
                <div style={{ width: '100%', margin: 'auto' }}>
                    {message && <p style={{ color: 'teal' }}>{message}</p>}
                </div>
                <input
                    style={{
                        margin: '5px',
                        height: '30px'
                    }}
                    type="email"
                    name="email"
                    placeholder="Enter Email"
                    value={email}
                    onChange={handleChange}
                />

                <div style={{ width: '100%', margin: 'auto' }}>
                    {error && <p style={{ color: 'red' }}>{error.graphQLErrors[0].message}</p>}
                </div>
                <button
                    style={{
                        margin: '5px',
                        padding: '10px',
                        background: 'teal',
                        color: 'white',
                        border: 'none',
                        cursor: 'pointer',
                        fontSize: '18px'
                    }}
                    type='submit'
                    disabled={loading}
                >Submit</button>
            </form>

        </div>
    )
}

export default RequestResetPassword;
