import React, { useContext} from 'react';
import { AuthContext } from '../contexts/AuthProvider';
import UserCartItems from './CartItems';


const UserCarts = () => {
    const { user } = useContext(AuthContext)
    console.log('Carts Data',  user.carts)
    return (
        user && <div className="Cart_Page">
        {user.carts.length === 0 ? <p>Your Cart is Empty</p> : <>
            <div className="cart_content" style={{ width: '50%', margin: 'auto' }}  >
        {/* Header */}
        <h3>List All My Cart Products</h3>
        <div style={{ display: 'grid', gridTemplateColumns: '2fr 2fr 2fr 2fr 2fr 2fr 2fr', width: '100%' }}>
            <h3 style={{ margin: 'auto' }}>Image</h3>
            <h3 style={{ margin: 'auto' }}>Name</h3>
            <h3 style={{ margin: 'auto' }}>Description</h3>
            <h3 style={{ margin: 'auto' }}>Price</h3>
            <h3 style={{ margin: 'auto' }}>Qualtity</h3>
            <h3 style={{ margin: 'auto' }}>Amonut</h3>
            <h3 style={{ margin: 'auto' }}>Actions</h3>
        </div>


        {/* Body */}

        <div>
            {
                user.carts.length > 0 &&
                user.carts.map(cart => (
                    <CartItems key={cart.id} key={cart.id} cart={cart} />
                ))}
        </div><br />
        <div
            style={{
                display: 'grid',
                gridTemplateColumns: '2fr 2fr 2fr 2fr 2fr 2fr 2fr',
                width: '100%',
            }}
        >
            <div style={{ margin: 'auto', alignItems: 'center', marginBottom: '5px' }}>Total Price:</div>
            <div style={{ margin: 'auto', alignItems: 'center', marginBottom: '5px' }}></div>
            <div style={{ margin: 'auto', alignItems: 'center', marginBottom: '5px' }}></div>
            <div style={{ margin: 'auto', alignItems: 'center', marginBottom: '5px' }}></div>
            <div style={{ margin: 'auto', alignItems: 'center', marginBottom: '5px' }}></div>
            <div style={{ margin: 'auto', alignItems: 'center', marginBottom: '5px', color: 'teal' }}>
                {
                    user.carts.length > 0 &&
                    user.carts.reduce(
                        (sum, cart) => sum + cart.qualtity * cart.product.price, 0
                    )}
            </div>
            <div style={{ margin: 'auto', alignItems: 'center', marginBottom: '5px' }}></div>
        </div>
    </div>
        </>}

</div>
    )
}

export default UserCarts;
