import React, { useContext } from 'react';
import Link from 'next/link';
import {useQuery} from '@apollo/react-hooks';
import {AuthContext} from '../contexts/AuthProvider';
import {QUERY_PRODUCTS} from '../graphql/Products';
import Router from 'next/router';
import '../styles/_products.scss';
import Header from './Header';

function Products() {
  const {data, loading, error} = useQuery(QUERY_PRODUCTS) //, {pollInterval:3000} Add pollInterval to refresh page per second
  const { user } = useContext(AuthContext)

  if(error) return <p>Ooops....! Something went wrong, Plz try again later</p>
  if(loading) return <p>loading...</p>
  return (
    <>
    <div className="header">
       <Header />
    </div>
    <div className="container">
      {data.products.map(prod => 
        <div className="product-container">
        <figure>
          { 
            user && user.id === prod.user.id ? 
                    <Link href='/manageProducts'>
                        <img src={prod.imageUrl} alt={prod.description}/>
                    </Link> : <Link href='/products/product_detail' as={`/products/${prod.id}`}>
                        <img src={prod.imageUrl} alt={prod.description}/>
                    </Link> 
          }
        </figure>
        <h3>{prod.name}</h3>
        <p className="description-text">
          {prod.description}
        </p>
        <p>{prod.price}-THB</p>
        {
            user && user.id === prod.user.id ? 
                <button className="btn-manage"
                  onClick={() => Router.push('/manageProducts')}
                >Manage Products</button> :
                <button className="btn-add-carts"
                onClick={() => Router.push('/carts')}
                >Add To Cart</button>
              } 
        </div>
      )}
    </div>
    </>
  )
}

export default Products;
