import gql from "graphql-tag";

export const ADD_TO_CART = gql`
      mutation ADD_TO_CART($id: ID!) {
            addToCart(id: $id) {
                  id
                  product {
                        id
                        name
                        description
                        imageUrl
                        price
                  }
                  qualtity
            }
      }
`;


