import React, { Component } from 'react'
import Backdrop from '../Backdrop';
import Modal from '../Modal';



export class CreatePro extends Component {

    state = {
        creating: false
    }


    CreateEventHandler = () => {
        this.setState({ creating: true })
    }

    modalConfirmHandler = () => {
        this.setState({ creating: true })
    }

    modalCancelHandler = () => {
        this.setState({ creating: false })
    }
    render() {
        return (
            <>
                { state.creating && <Backdrop />}
                { state.creating &&
                    <Modal title="Create Product" canCancel canConfirm onCancel={modalCancelHandler} onConfirm={modalConfirmHandler}>
                        <p>Modal content</p>
                    </Modal>}
            </>
        )
    }
}

export default CreatePro;
