import React, { useContext } from 'react';
import Link from 'next/link';
import {useQuery} from '@apollo/react-hooks';
import {AuthContext} from '../contexts/AuthProvider';
import {QUERY_PRODUCTS} from '../graphql/Products';
import Router from 'next/router';
import '../styles/_products.scss';

function Products() {
  const {data, loading, error} = useQuery(QUERY_PRODUCTS) //, {pollInterval:3000} Add pollInterval to refresh page per second
  const { user } = useContext(AuthContext)

  if(error) return <p>Ooops....! Something went wrong, Plz try again later</p>
  if(loading) return <p>loading...</p>
  return (
    <div className="products">
      {data.products.map(prod => 
            <div className="product_card" key={prod.id}>
                    <img src={prod.imageUrl} alt={prod.description} />

                    <div className="product_box">
                        <h3 title={prod.name} >{prod.name}</h3>
                        <span>${prod.price}</span>
                        <p>{prod.description}</p>
                    </div>
                    <div className="row_btn">
                        {  
                             user && user.id === prod.user.id ? 
                                     <button className="btn-manage"
                                       onClick={() => Router.push('/manageProducts')}
                                     >Manage</button> :
                                            <div className="btn-slot">
                                                <button className="btn-buy" onClick={() => Router.push('/carts')}>Buy Now!</button>
                                                <button className="btn-view">
                                                    <Link href='/products/product_detail' as={`/products/${prod.id}`}>
                                                    View</Link>
                                                </button>
                                            </div>

                        }
                    </div>
            </div>
      )}
    </div>
  )
}

export default Products;
