import React from 'react';
import Link from 'next/link';
import {useQuery} from '@apollo/react-hooks';
import gql from 'graphql-tag';



const QUERY_PRODUCTS = gql`
  query {
  products{
    id
    name
    description
    price
    createdAt
  }
} 
`
const fakeData = [
  {
    id: 1,
    name: "ມັງພຸດ",
    description: "ຜົນລະໄມ້ສົດຈາກສ່ວນ",
    price: 105,
    imageUrl:"https://images.unsplash.com/photo-1600453025590-f60d9d08ce73?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=700&q=80"
  },
  {
    id: 2,
    name: "ລຳໄຍ",
    description: "ຜົນລະໄມ້ສົດຈາກສ່ວນ",
    price: 125,
    imageUrl:"https://images.unsplash.com/photo-1600453025590-f60d9d08ce73?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=700&q=80"
  },
  {
    id: 3,
    name: "ທຸເລືອນ",
    description: "ຜົນລະໄມ້ທີກູຍັງງົງຄົນກິນມັນໄດ້ແນວໃດ",
    price: 135,
    imageUrl:"https://images.unsplash.com/photo-1600453025590-f60d9d08ce73?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=700&q=80"
  },
  {
    id: 4,
    name: "ທຸເລືອນ",
    description: "ຜົນລະໄມ້ທີກູຍັງງົງຄົນກິນມັນໄດ້ແນວໃດ",
    price: 135,
    imageUrl:"https://images.unsplash.com/photo-1600453025590-f60d9d08ce73?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=700&q=80"
  },
  {
    id: 5,
    name: "ທຸເລືອນ",
    description: "ຜົນລະໄມ້ທີກູຍັງງົງຄົນກິນມັນໄດ້ແນວໃດ",
    price: 135,
    imageUrl:"https://images.unsplash.com/photo-1600453025590-f60d9d08ce73?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=700&q=80"
  }
]
function products() {
  const {data, loading, error} = useQuery(QUERY_PRODUCTS)

  if(error) return <p>Ooops....! Something went wrong, Plz try again later</p>
  if(loading) return <p>loading...</p>
  // console.log('result', result)
  return (
    <div style={{display:'flex', justifyContent: 'space-around', alignItems:'center', marginTop: '40px'}}>
      {data.products.map(prod => 
          <div  key={prod.id}
            style={{
              display:'flex',
              flexDirection: 'column',
              alignItems:'center'
            }}
          >
            <Link href='/products/product_detail' as={`/products/${prod.id}`}>
              <a>
              <img src={prod.imageUrl} alt={prod.description} width="250px"/>
              </a>
            </Link>   
            <h3>{prod.name}</h3>
            <h4>{prod.price} THB</h4>
            <button style={{ background: 'green', color: 'white', padding: '10px', cursor:'pointer', border: 'none', borderRadius:'10px'}}>Add To Cart</button>
          </div> 
      )}
    </div>
  )
}

export default products
