import React from 'react';
import { Swiper, SwiperSlide} from 'swiper/react';
import SwiperCore, { Navigation, Pagination } from 'swiper';
import  'swiper/swiper-bundle.css';


SwiperCore.use([Navigation, Pagination])

const contentStyle = {
    height: '160px',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    background: '#364d79',
  };
const Banner = () => {

    const slides = [];

    for (let i= 0; i<5; i += 1){
        slides.push(
            <SwiperSlide key={`slide-${i}`} tag="li">
                <img className="slider_imgs" src={`https://picsum.photos/id/${i+1}/1375/300`} alt={`Slide ${i}`}/>
            </SwiperSlide>
        )
    }
    return (
        <div className="banner_header">
            <Swiper 
                    id="main" 
                    tag="section"
                    wrapperTag="ul"
                     navigation 
                     pagination 
                     spaceBetween={0} 
                     slidesPerView={1}
                     onInit={(swiper) => console.log('Swiper Initializatized', swiper)}
                     onSlideChange={(swiper) => { console.log('Slider index Changed to:', swiper.activeIndex)}}
                     onReachEnd={() => console.log('Swiper end reached')}
                     >
                    {slides}
            </Swiper>
            </div>
    )
}

export default Banner;
