import React, { useContext } from 'react';
import UserProductItems from './UserProductItem';
import { AuthContext } from '../contexts/AuthProvider';
import '../styles/_carts.scss';

function Cart() {
    const { user } = useContext(AuthContext)
    return (
        <div className="Cart_Page">

           <div className="cart_content" style={{ width: '50%', margin: 'auto' }}  >
            {/* Header */}
            <h3>List All My Cart Products</h3>
            <div style={{ display: 'grid', gridTemplateColumns: '2fr 2fr 2fr 2fr 2fr', width: '100%' }}>
                <h3 style={{ margin: 'auto' }}>Image</h3>
                <h3 style={{ margin: 'auto' }}>Name</h3>
                <h3 style={{ margin: 'auto' }}>Description</h3>
                <h3 style={{ margin: 'auto' }}>Price</h3>
                <h3 style={{ margin: 'auto' }}>Qualtity</h3>
                <h3 style={{ margin: 'auto' }}>Amonut</h3>
                <h3 style={{ margin: 'auto' }}>Actions</h3>
            </div>


            {/* Body */}

            {
                user &&
                user.carts.length > 0 &&
                user.carts.map(cart => (
                    <CartItems key={cart.id} key={cart.id} cart={cart} />
                ))}
        </div>
        </div>
    )
}

export default Cart
