import React, {useState} from 'react';
import {useMutation} from '@apollo/react-hooks';
import gql from 'graphql-tag';


const SIGN_UP = gql`
 mutation SIGN_UP($username: String!, $email: String!, $password: String!) {
  signup(
    username: $username
    email: $email
    password: $password
  ){
    id
    username
    email
  }
}
`


const Signup = () => {
  const [userInfo, setUserInfo] = useState({
    username: '',
    email: '',
    password: ''
  })
  const handleChange = e => {
    setUserInfo({
      ...userInfo,
      [e.target.username]: e.target.value
    })
  }
  const [signup, ] = useMutation(SIGN_UP, {variables: {...userInfo}})
  console.log(userInfo)
  return (
    <div style={{ margin: '100px'}}>
      <form action="" 
        style={{ 
          display:'flex', 
          flexDirection: 'column', 
          margin: 'auto', 
          width: '30%' 
        }}>
        <input 
          style={{ 
            margin: '5px', 
            height: '30px'
          }} 
          type="text" 
          name="username" 
          placeholder="Enter Username"
          onChange={handleChange}
          value={userInfo.username}
        />
        <input 
          style={{ 
            margin: '5px', 
            height: '30px'
          }} 
          type="email" 
          name="email" 
          placeholder="Enter Email"
          onChange={handleChange}
          value={userInfo.email}
        />
        <input 
          style={{ 
            margin: '5px', 
            height: '30px'
          }} 
          type="password" 
          name="password" 
          placeholder="Enter Password"
          onChange={handleChange}
          value={userInfo.password}
        />
        <button 
          style={{ 
            margin: '5px', 
            padding: '10px', 
            background: 'teal', 
            color:'white', 
            border:'none', 
            cursor: 'pointer',
            fontSize:'18px'
            }}
          >Submit</button>
      </form>
    </div>
  )
}

export default Signup
