import React from 'react';
import Link from 'next/link';
import {useQuery} from '@apollo/react-hooks';
import gql from 'graphql-tag';
//import apollo Client from Apollo Folder
import apolloClient from '../../apollo/apolloClient';


const QUERY_PRODUCTS = gql`
  query {
  products{
    id
    name
    description
    price
    imageUrl
    createdAt
  }
} 
`
function Products() {
  const {data, loading, error} = useQuery(QUERY_PRODUCTS, {pollInterval:3000})

  if(error) return <p>Ooops....! Something went wrong, Plz try again later</p>
  if(loading) return <p>loading...</p>
  return (
    <div style={{display:'grid', gridTemplateColumns: '1fr 1fr 1fr 1fr 1fr', marginTop: '40px', gridGap:'75px'}}>
      {data.products.map(prod => 
          <div  key={prod.id}
            style={{
              display:'flex',
              flexDirection: 'column',
              alignItems:'center',
              marginBottom: '8px',
              border: 'solid 0.5px black',
              padding: '15px'
            }}
          >
            <Link href='/products/product_detail' as={`/products/${prod.id}`}>
              <a>
              <img src={prod.imageUrl} alt={prod.description} width="250px"/>
              </a>
            </Link>   
            <h3>{prod.name}</h3>
            <h4>{prod.price} THB</h4>
            <button style={{ 
              background: 'green', 
              color: 'white', 
              padding: '10px', 
              cursor:'pointer', 
              border: 'none', 
              borderRadius:'10px'
            }}>Add To Cart</button>
          </div> 
      )}
    </div>
  )
}

export default apolloClient(Products)
