import React, {useContext, useEffect} from 'react'; //user context provider to protect Route
import Router from 'next/router'

import {AuthContext} from '../contexts/AuthProvider';

function carts() {
  const {user} = useContext(AuthContext);

  //use Effect protect to run access first time
  useEffect(() =>{
    if(!user) {
      Router.push('/signin')
    }
  }, [])
  return (
    <div>
      Cart Page
    </div>
  )
}

export default carts
