import React, {useState, useContext} from 'react';
import { useQuery } from '@apollo/react-hooks';
import { MY_PROFILE } from '../../graphql/Profile';


import { Avatar } from 'antd';

const Sidebar = () => {

    const { data, loading, error } = useQuery(MY_PROFILE)
    console.log("User:", data)

    const [nav] = useState([ //, setNav put after Nav
        { lable:"DASHBOARD", slug:"/dashboard", icon: "ionicons icon icon-home" },
        { lable:"ORDERS", slug:"/discover", icon: "ionicons icon icon-quill" },
        { lable:"MY PRODUCTS", slug:"/manage", icon: "ionicons icon icon-pen" },
        { lable:"CATEGORIES", slug:"/categories", icon: "ionicons icon icon-pen" },
        { lable:"BRANDS", slug:"/brands", icon: "ionicons icon icon-pen" },
        { lable:"TAGS", slug:"/tags", icon: "ionicons icon icon-droplet" },
        { lable:"BANK ACCOUNT", slug:"/banks", icon: "ionicons icon icon-pencil" },
        { lable:"DELIVERY", slug:"/deliveries", icon: "ionicons icon icon-newspaper" },
        { lable:"PROFILE", slug:"/profile", icon: "ionicons icon icon-droplet" }
    ])



     // Set Sidebar label slug constant variable to Currently Page
     const [currentPage] = useState("/") //, setCurrenPage put after currentpage

        // Loop Sidebar label slug constan variable
    var navigation = [];
    for(let i = 0; i < nav.length; i++) {
        navigation.push(
                <li key={"nav-" + i + "-" + nav[i].slug}>
                    <a href={nav[i].slug} className={"link" + (currentPage === nav[i].slug ? " on" : "") }>
                        <div className={"ico s16  " + nav[i].icon} />
                        <h3 className="lbl s18 fontn">{nav[i].lable}</h3>
                    </a>
                </li>
        );
    }
    if (error) return <p>Oooops...Something went wrong!</p>
    if (loading) return <p>Loading...!</p>
  return (
    <div className="sidebar">
        <div className="profile_image">
                <Avatar src={data.user.profile.profileImage} size={80} /><br/>
                <span className="menu-avatar-name">@{data.user.username}</span>
        </div>
            <ul className="nav">
                    {navigation} <br/>
            </ul>
            <ul className="setting">
            User Setting Here
            </ul>
    </div>
  )
}

export default Sidebar;
