import gql from 'graphql-tag';


export const QUERY_USER = {
  query: `
    query {
      user{
        id
        username
        email
        products{
          id
        }
        carts{
          id
          product{
            name
            price
            imageUrl
          }
          qualtity
        }
      }
    }
  `
}


export const SIGN_UP = gql`
mutation SIGN_UP($username: String!, $email: String!, $password: String!) {
 signup(username: $username, email: $email, password: $password
 ){
   id
   username
   email
 }
}
`
