import React, { Component } from 'react'
import ProductList from './ProductList';
import Backdrop from '../Backdrop';
import Modal from '../Modal';


class UserProduct extends Component {
    state = {
        creating: false
    }

    CreateEventHandler = () => {
        this.setState({ creating: true })
    }

    modalConfirmHandler = () => {
        this.setState({ creating: true })
    }

    modalCancelHandler = () => {
        this.setState({ creating: false })
    }

    render() {
        return (
            <div>
                <h2 className="dash-title">All Products List</h2>
                <button onClick={this.CreateEventHandler}>
                    <span className="ti-plus"></span>
                    <small>Add Product</small>
                </button>
                { this.state.creating && <Backdrop />}
                { this.state.creating &&
                    <Modal title="Create Product" canCancel canConfirm onCancel={this.modalCancelHandler} onConfirm={this.modalConfirmHandler}>
                        <p>Modal content</p>
                    </Modal>}
                <ProductList />
            </div>
        )
    }
}

export default UserProduct;
