import React from 'react';

const modal = props => (
    <div className="modal">
        <section className="modal__header">
            <h1>{props.title}</h1>
        </section>
        <section className="modal__content">
            {props.children}
        </section>
        <section className="modal__action">
            {props.canCancel && 
                (<button 
                    className="btn__cancel" 
                    onClick={props.canCancel}
                >Cancel</button>)}
            {props.canConfirm && 
             (   <button 
                    className="btn__confirm" 
                    onClick={props.canConfirm}
                >Confirm</button>)}
        </section>
    </div>
)
export default modal;
